func:
// Langų saugiklis: pradėti 10 min laikmatį
//
// Atsakomybė:
// - Reaguoja į binary_sensor.langai būsenos pokytį (on/off)
// - Išsaugo tikslinę būseną į flow.windows_guard_target
// - Paleidžia 10 min triggerį, po kurio bus patikrinta ar langai vis dar atidaryti
//
// Pastabos:
// - Tikslas “on” reiškia: po 10 min tikrinsim ar langai vis dar on → tada kelsim windows_long_open
// - Tikslas “off” reiškia: po 10 min patvirtinsim, kad langai uždaryti → windows_long_open nuimsim
// - Jei gaunama kita būsena (unknown/unavailable) – nieko nedarom

var st = msg?.payload?.new_state?.state ? String(msg.payload.new_state.state) : null;
if (!st || st === 'unknown' || st === 'unavailable') return null;

if (st === 'on') {
  flow.set('windows_guard_target', 'on');
  return { topic: 'windows_guard', payload: 'start' };
}
if (st === 'off') {
  flow.set('windows_guard_target', 'off');
  return { topic: 'windows_guard', payload: 'start' };
}
return null;

var g = global.get('homeassistant') || {};
var haStates = g.homeAssistant && g.homeAssistant.states ? g.homeAssistant.states : (g.homeassistant && g.homeassistant.states ? g.homeassistant.states : (g.states || {}));
var cur = haStates['binary_sensor.langai'] ? String(haStates['binary_sensor.langai'].state) : null;
var target = flow.get('windows_guard_target');
if (target === 'on') {
  flow.set('windows_long_open', cur === 'on');
} else if (target === 'off') {
  flow.set('windows_long_open', false);
}
msg.topic = 'windows_guard_applied';
return msg;

if (msg.topic === 'force_night') flow.set('force_mode','night');
else if (msg.topic === 'force_day') flow.set('force_mode','day');
else if (msg.topic === 'force_clear') flow.set('force_mode', null);
return {topic:'run'};

// ===== Būsenų nuskaitymas (patikimas) =====
//
// Šaltiniai ir logika:
// - Home Assistant būsenos iš global konteinerio (keli fallback keliai)
// - Dienos / nakties nustatymas pagal Europe/Vilnius (22:00–07:00)
// - force_mode testams ("night" | "day" | null)
// - Signalizacijos fallback po HA restarto (unknown/unavailable -> naudojama paskutinė žinoma būsena)

var g = global.get('homeassistant') || {};
var haStates =
  (g.homeAssistant && g.homeAssistant.states) ? g.homeAssistant.states :
    ((g.homeassistant && g.homeassistant.states) ? g.homeassistant.states :
      (g.states || {}));

// Subjektų (entity) žemėlapis
var ENT = {
  alarm: 'alarm_control_panel.home',
  windows: 'binary_sensor.langai',
  co2: 'sensor.oro_stotele_carbon_dioxide',
  outdoor: 'sensor.boiler_outside_temperature',
  humidity: 'sensor.bathroom_humidity',
  stopflags: 'sensor.alarm_status_stop_flags',
  recup: 'switch.start_stop'
};

// Pagalbinės funkcijos
function isBad(v) {
  if (v === null || v === undefined) return true;
  var s = String(v).toLowerCase();
  return (s === 'unknown' || s === 'unavailable');
}

function isNum(v) {
  return v !== null && v !== undefined && isFinite(v);
}

function num(ent) {
  if (!haStates[ent]) return null;
  var raw = haStates[ent].state;
  if (isBad(raw)) return null;
  var v = parseFloat(raw);
  return isFinite(v) ? v : null;
}

function str(ent) {
  if (!haStates[ent]) return null;
  var raw = haStates[ent].state;
  if (isBad(raw)) return null;
  return String(raw);
}

// Sudedam msg.state struktūrą
msg.state = {
  alarm: str(ENT.alarm),
  windows: str(ENT.windows),
  windows_long_open: !!flow.get('windows_long_open'),
  co2: num(ENT.co2),
  outdoor: num(ENT.outdoor),
  humidity: num(ENT.humidity),
  stopflags: str(ENT.stopflags),
  recup: str(ENT.recup)
};

// Signalizacijos fallback po HA restarto
// Jei alarm yra unknown/unavailable -> msg.state.alarm bus null (per str())
// Tada naudojam paskutinę žinomą būseną iš flow.
if (msg.state.alarm) {
  flow.set('alarm_last_known', msg.state.alarm);
} else {
  var lastAlarm = flow.get('alarm_last_known');
  if (lastAlarm) {
    msg.state.alarm = lastAlarm;
    msg.state._alarm_cached = true;
  }
}

// Rekuperatoriaus įjungimo būsena (boolean)
msg.state.recup_is_on =
  (String(msg.state.recup || '').toLowerCase() === 'on');

// Dienos / nakties logika pagal Europe/Vilnius (22:00–07:00)
var tz = "Europe/Vilnius";

var parts = new Intl.DateTimeFormat('en-GB', {
  timeZone: tz,
  hour: '2-digit',
  minute: '2-digit',
  hour12: false
}).formatToParts(new Date());

var hh = Number(parts.find(function (p) { return p.type === 'hour'; }).value);
var mm = Number(parts.find(function (p) { return p.type === 'minute'; }).value);

var minutes = hh * 60 + mm;
var isNight = (minutes >= 22 * 60 || minutes < 7 * 60);

// Testinis override
var force = flow.get('force_mode'); // "night" | "day" | null
if (force === 'night') isNight = true;
if (force === 'day') isNight = false;

msg.state.is_night = isNight;
msg.state.is_day = !isNight;

// Laiko žyma LT laiku (patogu debug'ui)
msg.state.now = new Intl.DateTimeFormat('sv-SE', {
  timeZone: tz,
  year: 'numeric',
  month: '2-digit',
  day: '2-digit',
  hour: '2-digit',
  minute: '2-digit',
  second: '2-digit',
  hour12: false
}).format(new Date()).replace(' ', 'T');

// STOP FLAGS aktyvumas
var stopflagsRaw = msg.state.stopflags ? String(msg.state.stopflags) : '';
var stopflags = stopflagsRaw.trim().toLowerCase();
var stopActive =
  stopflags &&
  ['none', 'unknown', 'unavailable', ''].indexOf(stopflags) === -1;

// Node status (trumpa santrauka)
var statusParts = [];

statusParts.push(isNight ? "NIGHT" : "DAY");

if (isNum(msg.state.co2)) {
  statusParts.push("CO2 " + Math.round(msg.state.co2));
}

if (isNum(msg.state.outdoor)) {
  statusParts.push("OUT " + msg.state.outdoor.toFixed(1) + "°");
}

if (isNum(msg.state.humidity)) {
  statusParts.push("HUM " + msg.state.humidity.toFixed(0) + "%");
}

if (msg.state.windows_long_open) {
  statusParts.push("WIN_LONG");
}

if (stopActive) {
  statusParts.push("STOP!");
}

if (msg.state.alarm) {
  var alarmText = "AL:" + msg.state.alarm;
  if (msg.state._alarm_cached) alarmText += " (cached)";
  statusParts.push(alarmText);
}

statusParts.push(msg.state.recup_is_on ? "RECUP ON" : "RECUP OFF");

var color =
  stopActive ? "red" :
    msg.state.windows_long_open ? "yellow" :
      "green";

node.status({
  fill: color,
  shape: "dot",
  text: statusParts.join(" | ")
});

return msg;

// ===== Sprendimas: norima būsena (Decide desired) =====
//
// Reikalavimai iš flow:
// - msg.state: { alarm, windows_long_open, co2, outdoor, humidity, stopflags, is_night, ... }
// - CO₂ laiptai: <600=20, 600–749=30, 750–899=45, 900–1099=70, >=1100=100
// - ARMED_AWAY boost: CO₂>=800 vieną kartą (desired.armedBoost=true -> trigger node užkurs 10 min OFF)
//
// Pastabos:
// - STOP FLAGS = aukščiausias prioritetas (hard block)
// - Vonios OVR: ON >80, OFF tik jei <70 išsilaiko 5 min
// - Langai ilgiau 10 min -> OFF (jei nėra STOP FLAGS)
// - Day >18°C: jungiam tik jei CO₂>=800, kitu atveju OFF
// - Rate-based boost: pakelia fan, jei CO₂ kyla greitai, su hold
// - Saugiklis: rate-based boost išjungtas, jei CO₂ < 600 (single/low-CO₂ režimui)

var s = msg.state || {};
var alarm = s.alarm ? String(s.alarm) : '';
var windowsLong = !!s.windows_long_open;

var co2 = s.co2;
var outdoor = s.outdoor;
var hum = s.humidity;

var isNight = !!s.is_night;

// Pagalbinės funkcijos
function isNum(v) { return v !== null && v !== undefined && isFinite(v); }

function co2ToFan(v) {
  if (!isNum(v)) return 30;
  if (v < 600) return 20;
  if (v < 750) return 30;
  if (v < 900) return 45;
  if (v < 1100) return 70;
  return 100;
}

// STOP FLAGS
var stopflagsRaw = s.stopflags ? String(s.stopflags) : '';
var stopflags = stopflagsRaw.trim().toLowerCase();
var stopActive = stopflags && ['none', 'unknown', 'unavailable', ''].indexOf(stopflags) === -1;

var lastStop = flow.get('last_stopflags') || '';
flow.set('last_stopflags', stopflags);

// Numatyta būsena
var desired = { on: true, fan: 30, ovr: false, notify: null, armedBoost: false };

// Debug / priežastis
var reason = "DEFAULT";
var dbg = {};

// 1) STOP FLAGS (hard block)
if (stopActive) {
  desired.on = false;
  desired.fan = null;
  desired.ovr = false;
  reason = "STOPFLAGS";

  if (stopflags !== lastStop) {
    desired.notify =
      'Komfovent STOP FLAGS: ' + (stopflagsRaw || '') +
      '. Išjungiu rekuperatorių ir stabdau automatiką.';
  }

} else {

  // 2) Vonios OVR (histerezė)
  var now = Date.now();
  var ovrActive = !!flow.get('bath_ovr_active');
  var offSince = flow.get('bath_ovr_off_since') || null;

  if (isNum(hum)) {
    if (hum > 80) {
      flow.set('bath_ovr_active', true);
      flow.set('bath_ovr_off_since', null);
    }

    if (ovrActive) {
      if (hum < 70) {
        if (!offSince) {
          flow.set('bath_ovr_off_since', now);
        } else if ((now - offSince) >= 5 * 60 * 1000) {
          flow.set('bath_ovr_active', false);
          flow.set('bath_ovr_off_since', null);
        }
      } else {
        flow.set('bath_ovr_off_since', null);
      }
    }
  }

  var bathOvr = !!flow.get('bath_ovr_active');
  dbg.bath_ovr_active = bathOvr;

  // 3) ARMED_AWAY logika
  if (alarm !== 'armed_away') {
    flow.set('armed_boost_done', false);
  }

  if (alarm === 'armed_away') {
    var boostDone = !!flow.get('armed_boost_done');
    dbg.armed_boost_done = boostDone;

    if (!boostDone && isNum(co2) && co2 >= 800) {
      flow.set('armed_boost_done', true);
      desired.on = true;
      desired.fan = co2ToFan(co2);
      desired.ovr = false;
      desired.armedBoost = true;
      reason = "ARMED_AWAY_BOOST";
    } else {
      desired.on = false;
      desired.fan = null;
      desired.ovr = false;
      reason = "ARMED_AWAY_OFF";
    }

  } else if (bathOvr) {
    desired.on = true;
    desired.fan = 100;
    desired.ovr = true;
    reason = "BATH_OVR";

  } else if (windowsLong) {
    desired.on = false;
    desired.fan = null;
    desired.ovr = false;
    reason = "WINDOWS_LONG";

  } else {

    // 6) Pagrindinis režimas (diena / naktis)
    if (isNight) {
      desired.on = true;
      desired.fan = 35;
      if (isNum(co2)) desired.fan = Math.max(35, co2ToFan(co2));
      reason = "NIGHT";
    } else {
      if (isNum(outdoor) && outdoor > 18) {
        if (isNum(co2) && co2 >= 800) {
          desired.on = true;
          desired.fan = co2ToFan(co2);
          reason = "DAY_HOT_CO2_HIGH";
        } else {
          desired.on = false;
          desired.fan = null;
          desired.ovr = false;
          reason = "DAY_HOT_OFF";
        }
      } else {
        desired.on = true;
        desired.fan = co2ToFan(co2);
        reason = "DAY";
      }
    }
  }

  // 7) Rate-based boost (CO₂ kilimo greitis)
  if (
    desired.on === true &&
    isNum(desired.fan) &&
    alarm !== 'armed_away' &&
    desired.ovr !== true
  ) {
    var nowTs = Date.now();
    var co2now = isNum(co2) ? Number(co2) : null;

    var RATE_MAX_AGE_MS = 15 * 60 * 1000;
    var RATE_DEADBAND = 2;     // ppm/min
    var BOOST_HOLD_MIN = 10;   // min
    var BOOST_MAX_FAN = 80;

    var prev = flow.get('co2_prev') || null;
    var rate = null; // ppm/min

    if (co2now !== null && prev && isNum(prev.value) && isNum(prev.ts)) {
      var ageMs = nowTs - prev.ts;
      var dtMin = ageMs / 60000;
      if (ageMs > 0 && ageMs <= RATE_MAX_AGE_MS && dtMin > 0) {
        rate = (co2now - prev.value) / dtMin;
      }
    }

    if (co2now !== null) flow.set('co2_prev', { value: co2now, ts: nowTs });

    if (rate !== null && Math.abs(rate) < RATE_DEADBAND) rate = 0;

    var boostFan = null;

    // Saugiklis: boost leidžiamas tik jei CO₂ >= 600
    if (rate !== null && isNum(co2) && co2 >= 600) {
      if (rate >= 30) boostFan = 80;
      else if (rate >= 18) boostFan = 65;
      else if (rate >= 10) boostFan = 55;
    }

    var holdUntil = flow.get('rate_boost_hold_until') || 0;

    if (boostFan !== null) {
      flow.set('rate_boost_hold_until', nowTs + BOOST_HOLD_MIN * 60 * 1000);
      flow.set('rate_boost_last_fan', boostFan);
    } else if (nowTs < holdUntil) {
      boostFan = flow.get('rate_boost_last_fan') || null;
    } else {
      flow.set('rate_boost_last_fan', null);
    }

    if (boostFan !== null) {
      var before = desired.fan;
      desired.fan = Math.max(desired.fan, Math.min(BOOST_MAX_FAN, boostFan));
      if (desired.fan !== before) {
        reason = reason + "+RATE_BOOST";
      }
    }

    msg._rate = rate;
    msg._rate_boostFan = boostFan;
  }
}

// Normalizavimas
if (desired.on === false) {
  desired.fan = null;
  desired.ovr = false;
  desired.armedBoost = false;
}

// Priskiriam į msg
msg.desired = desired;
msg.reason = reason;
msg.debug = dbg;

// Laiko žymos (debug / log)
msg.desired_ts = Date.now();
msg.desired_at = new Date(msg.desired_ts).toISOString();

// Node status
var parts = [];
parts.push(reason);

parts.push((desired.on ? "ON" : "OFF") + (desired.ovr ? " OVR" : ""));
if (isNum(desired.fan)) parts.push("FAN " + desired.fan);

if (isNum(co2)) parts.push("CO2 " + Math.round(co2));
if (isNum(outdoor)) parts.push("OUT " + outdoor.toFixed(1));
if (isNum(hum)) parts.push("HUM " + hum.toFixed(1));

if (windowsLong) parts.push("WIN_LONG");
if (alarm) parts.push("AL:" + alarm);

var fill = "green";
if (stopActive) fill = "red";
else if (desired.ovr) fill = "blue";
else if (alarm === "armed_away") fill = "orange";
else if (windowsLong) fill = "yellow";

node.status({ fill: fill, shape: "dot", text: parts.join(" | ") });

return msg;

// ===== ARMED_AWAY boost šaka =====
// Paskirtis:
// - Jei Decide node nustatė desired.armedBoost = true,
//   paleidžiame atskirą trigger srautą (pvz., 10 min OFF ciklui).
// - OUT1: visada perduodame originalų msg toliau
// - OUT2: siunčiame signalą tik jei reikia startuoti boost ciklą

var d = msg.desired || {};

// Jei pažymėtas vienkartinis ARMED_AWAY boost
if (d.armedBoost === true) {
  return [
    msg,
    { topic: "armed_boost_start" }
  ];
}

// Kitu atveju – tik pagrindinė šaka
return [msg, null];

// ===== Priverstinis išjungimas po ARMED boost =====
// Šis mazgas sukuria laikiną "desired" būseną,
// kuri priverstinai išjungia rekuperatorių
// pasibaigus 10 min boost laikotarpiui.

msg.desired = {
    on: false,
    fan: null,
    ovr: false,
    notify: null,
    armedBoost: false
};

return msg;

// ===== Normalize + build desired_key =====
// Paskirtis:
// - Normalizuoti msg.desired reikšmes
// - Užtikrinti, kad OFF būsenoje fan/ovr būtų null/false
// - Sugeneruoti stabilų desired_key RBE filtrui
// - Į raktą įtraukti realią recup būseną,
//   kad po rankinio įjungimo ar disarm komanda "atsimuštų"

const d = msg.desired || {};
const s = msg.state || {};

// ---- Normalizacija ----
if (d.on === false) {
    d.fan = null;
    d.ovr = false;
}

// ---- Realios įrenginio būsenos įtraukimas ----
const recup_is_on = (s.recup_is_on === true);

// ---- Stabilus raktas RBE filtrui ----
msg.desired_key = JSON.stringify({
    on: d.on === true,
    fan: (d.fan === null || d.fan === undefined)
        ? null
        : Math.round(d.fan),
    ovr: d.ovr === true,
    autostart_delay: d.autostart_delay === true,
    notify: d.notify ? "1" : "0",
    recup_is_on: recup_is_on
});

// Atnaujintas desired objektas perduodamas toliau
msg.desired = d;

return msg;

// ===== Veiksmų sąrašo sudarymas (msg.payload = array) =====
// Paskirtis:
// 1) Iš msg.desired suformuoja nuoseklų veiksmų sąrašą (actions[]).
// 2) Vėliau šis sąrašas išskaidomas per Split ir nukreipiamas į HA call-service mazgus.
//
// Pastabos:
// - Ventiliatoriaus reikšmė ribojama 20–100%.
// - OVR relė valdoma atskirai nuo start/stop.
// - Notifikacija pridedama tik jei d.notify turi tekstą.

var d = msg.desired || {};

var ENT = {
  start_stop: 'switch.start_stop',
  intake: 'number.intake_level_1',
  exhaust: 'number.exhaust_level_1',
  ovr_relay: 'switch.recup_switch_relay',
  notify: 'notify.mobile_app_g_iphone'
};

var actions = [];

// --- 1) Start/Stop ---
if (d.on === true) {
  actions.push({ kind: 'switch_on', entity_id: ENT.start_stop });
} else {
  actions.push({ kind: 'switch_off', entity_id: ENT.start_stop });
}

// --- 2) Ventiliatoriaus lygiai (intake/exhaust) ---
if (d.on === true && d.fan !== null && d.fan !== undefined) {
  var v = Math.max(20, Math.min(100, Math.round(d.fan)));
  actions.push({ kind: 'set_number', entity_id: ENT.intake, value: v });
  actions.push({ kind: 'set_number', entity_id: ENT.exhaust, value: v });
}

// --- 3) OVR relė ---
if (d.ovr === true) {
  actions.push({ kind: 'switch_on', entity_id: ENT.ovr_relay });
} else {
  actions.push({ kind: 'switch_off', entity_id: ENT.ovr_relay });
}

// --- 4) Notifikacija ---
if (d.notify) {
  actions.push({
    kind: 'notify',
    entity_id: ENT.notify,
    title: 'Komfovent',
    message: d.notify
  });
}

msg.payload = actions;
return msg;

// Paruošia payload switch.turn_on servisui

var a = msg.payload;

// Patikra: turi būti objektas su entity_id
if (!a || !a.entity_id) {
    return null;
}

// Formuojam HA servisui tinkamą payload
msg.payload = {
    entity_id: a.entity_id
};

return msg;

// Paruošia payload switch.turn_off servisui

var a = msg.payload;

// Patikra: turi būti objektas su entity_id
if (!a || !a.entity_id) {
    return null;
}

// Formuojam HA servisui tinkamą payload
msg.payload = {
    entity_id: a.entity_id
};

return msg;

// Paruošia payload number.set_value servisui

var a = msg.payload;

// Patikra: turi būti objektas su entity_id ir value
if (!a || !a.entity_id || a.value === undefined || a.value === null) {
    return null;
}

// Konvertuojam į skaičių
var val = Number(a.value);
if (!isFinite(val)) {
    return null;
}

// Formuojam HA servisui tinkamą payload
msg.payload = {
    entity_id: a.entity_id,
    value: val
};

return msg;

// Paruošia payload notify servisui

var a = msg.payload;

// Turi būti objektas su bent message arba tekstas
if (!a) return null;

var title = "Komfovent";
var message = null;

// Jei objektas
if (typeof a === "object") {
    if (a.title && String(a.title).trim() !== "") {
        title = String(a.title);
    }

    if (a.message && String(a.message).trim() !== "") {
        message = String(a.message);
    }
}
// Jei paprastas tekstas
else {
    message = String(a);
}

// Jei nėra žinutės – nieko nesiunčiam
if (!message) return null;

msg.payload = {
    title: title,
    message: message
};

return msg;

// ===== Rate telemetry =====
// Paskirtis:
// 1) Kaupti CO₂ kilimo greičio istoriją (flow.rate_log) kalibravimui
// 2) Maitinti Home Assistant subjektus per išėjimus:
//    - OUT1: sensor.current_co2_rate (ppm/min)
//    - OUT2: binary_sensor.rate_boost_active
//    - OUT4: sensor.komfovent_logic_mode
// Pastaba: HA entity konfigūravimas vyksta HA sensor/binary_sensor node'uose, ne čia.

// --- Pagalbinės funkcijos ---
function isNum(v) {
    return v !== null && v !== undefined && isFinite(v);
}

function normStr(v) {
    if (v === null || v === undefined) return "";
    return String(v).trim();
}

function isStopActive(stopflagsRaw) {
    const s = normStr(stopflagsRaw).toLowerCase();
    return s !== "" && s !== "none" && s !== "unknown" && s !== "unavailable";
}

// --- Įvestys ---
const rate = msg._rate;              // ppm/min (gali būti null)
const boostFan = msg._rate_boostFan; // null arba skaičius

const s = msg.state || {};
const d = msg.desired || {};

// --- Logikos režimo kodas (internal) ---
let modeCode = "UNKNOWN";

if (isStopActive(s.stopflags)) {
    modeCode = "STOP";
} else if (d.ovr === true) {
    modeCode = "OVR";
} else if (s.alarm === "armed_away") {
    modeCode = "ARMED_AWAY";
} else if (s.windows_long_open === true) {
    modeCode = "WINDOWS";
} else if (s.is_night === true) {
    modeCode = "NIGHT";
} else {
    modeCode = "DAY";
}

// Jei šiame cikle aktyvus rate-boost, rodome atskirą režimą
if (isNum(boostFan)) {
    modeCode = "RATE_BOOST";
}

// --- Lietuviški režimų pavadinimai (HA atvaizdavimui) ---
const LT = {
    STOP: "STOP",
    OVR: "OVR",
    ARMED_AWAY: "Išvykę",
    WINDOWS: "Langai",
    NIGHT: "Naktis",
    DAY: "Diena",
    RATE_BOOST: "CO₂ kilimas",
    UNKNOWN: "Laukiama"
};

const modeLt = LT[modeCode] || modeCode;

// --- Telemetrijos log'as (kalibravimui) ---
// Log'iname tik tada, kai rate realiai paskaičiuotas
if (isNum(rate)) {
    const entry = {
        ts: Date.now(),
        rate_ppm_min: Number(rate),
        boost_fan: isNum(boostFan) ? Number(boostFan) : null,
        co2: isNum(s.co2) ? Number(s.co2) : null,
        outdoor: isNum(s.outdoor) ? Number(s.outdoor) : null,
        humidity: isNum(s.humidity) ? Number(s.humidity) : null,
        desired_on: d.on === true,
        desired_fan: isNum(d.fan) ? Number(d.fan) : null,
        desired_ovr: d.ovr === true,
        windows_long_open: s.windows_long_open === true,
        alarm: s.alarm ? String(s.alarm) : "",
        logic_mode: modeCode
    };

    let log = flow.get("rate_log") || [];
    if (!Array.isArray(log)) log = [];
    log.push(entry);

    // Retencija: ribojame taškų kiekį (apytiksliai ~14 dienų, jei skaičiuojama kas ~10 min)
    const MAX_POINTS = 2200;
    while (log.length > MAX_POINTS) log.shift();

    flow.set("rate_log", log);

    // Papildoma informacija debug'ui (nebūtina naudoti)
    msg._rate_telemetry = { points: log.length, last: entry };
}

// --- Išėjimų žinutės ---
// OUT1: CO₂ kilimo greitis (ppm/min)
// Jei nėra rate — negrąžiname reikšmės, kad HA negaus "0" ir nebus šiukšlių grafike
let out1 = null;
if (isNum(rate)) {
    out1 = {
        payload: Number(rate.toFixed(2)),
        attributes: {
            unit: "ppm/min",
            co2: isNum(s.co2) ? Number(s.co2) : null,
            boost_fan: isNum(boostFan) ? Number(boostFan) : null
        }
    };
}

// OUT2: Ar aktyvus rate-boost
const out2 = { payload: isNum(boostFan) };

// OUT3: Rezervuota (nenaudojama)
const out3 = null;

// OUT4: Logikos režimas (LT) + atributai (kodas ir kontekstas)
const out4 = {
    payload: modeLt,
    attributes: {
        mode_code: modeCode,
        rate_ppm_min: isNum(rate) ? Number(rate.toFixed(2)) : null,
        boost_fan: isNum(boostFan) ? Number(boostFan) : null,
        co2: isNum(s.co2) ? Number(s.co2) : null,
        is_night: s.is_night === true,
        windows_long_open: s.windows_long_open === true,
        alarm: s.alarm ? String(s.alarm) : "",
        ovr: d.ovr === true
    }
};

// --- Node status ---
node.status({
    fill: isNum(boostFan) ? "red" : "blue",
    shape: "dot",
    text: `${modeLt} | rate ${isNum(rate) ? rate.toFixed(1) : "-"} ppm/min`
});

return [out1, out2, out3, out4];
