func:
// Global nustatymai vienoje vietoje (naudojami visuose flow)
const schema = 5;
const ver = '4.3.0';

global.set('schema_ver', schema);
global.set('system_version', ver);

// Vieningi HA entity ID
global.set('komfovent_entities', {
  mode: 'sensor.komfovent_logic_2',
  boost_active_ha: 'binary_sensor.rate_boost_2',
  boost_active_internal: 'binary_sensor.komfovent_rate_boost_nr',
  fan_actual: 'sensor.intake_fan_level_current',
  fan_desired: 'sensor.komfovent_fan_desired',
  alarm: 'alarm_control_panel.home',
  co2: 'sensor.oro_stotele_carbon_dioxide',
  outdoor: 'sensor.boiler_outside_temperature',
  humidity: 'sensor.bathroom_humidity',
  windows: 'binary_sensor.langai',
  recup: 'switch.start_stop',
  stopflags: 'sensor.alarm_status_stop_flags'
});

global.set('ai_config', {
  provider: 'gemini',
  enabled: false,
  telegram_enabled: false
});

// PATAISYTA VIETA: naudojame ką tik nustatytus kintamuosius
node.status({
  fill: 'green',
  shape: 'dot',
  text: `globals set: schema ${schema}, ver ${ver}`
});

return null;

// Būsenų nuskaitymas telemetrijai (patikimas)
// - Skaitom iš Home Assistant global cache
// - Jei alarm unknown/unavailable: naudojam paskutinę žinomą
// - Mode/logic imam iš sensor.komfovent_logic_2 (source of truth)

var g = global.get('homeassistant') || {};
if (typeof g !== 'object' || g === null) g = {};

var haStates =
  (g.homeAssistant && g.homeAssistant.states) ? g.homeAssistant.states :
    ((g.homeassistant && g.homeassistant.states) ? g.homeassistant.states :
      (g.states || {}));

var ENT = {
  alarm: 'alarm_control_panel.home',
  co2: 'sensor.oro_stotele_carbon_dioxide',
  outdoor: 'sensor.boiler_outside_temperature',
  humidity: 'sensor.bathroom_humidity',
  windows: 'binary_sensor.langai',
  stopflags: 'sensor.alarm_status_stop_flags',
  recup: 'switch.start_stop',
  mode: 'sensor.komfovent_logic_2'
};

function isBad(v) {
  if (v === null || v === undefined) return true;
  var s = String(v).trim().toLowerCase();
  return (s === '' || s === 'unknown' || s === 'unavailable');
}

function num(ent) {
  if (!haStates || !haStates[ent]) return null;
  var raw = haStates[ent].state;
  if (isBad(raw)) return null;
  var v = parseFloat(raw);
  return isFinite(v) ? v : null;
}

function str(ent) {
  if (!haStates || !haStates[ent]) return null;
  var raw = haStates[ent].state;
  if (isBad(raw)) return null;
  return String(raw);
}

msg.state = {
  alarm: str(ENT.alarm),
  co2: num(ENT.co2),
  outdoor: num(ENT.outdoor),
  humidity: num(ENT.humidity),
  windows: str(ENT.windows),
  windows_long_open: !!flow.get('windows_long_open'),
  stopflags: str(ENT.stopflags),
  recup: str(ENT.recup),
  mode: str(ENT.mode)
};

// Alarm fallback
if (msg.state.alarm) {
  flow.set('alarm_last_known', msg.state.alarm);
} else {
  var lastAlarm = flow.get('alarm_last_known');
  if (lastAlarm) msg.state.alarm = lastAlarm;
}

// Mode fallback
if (msg.state.mode) {
  flow.set('mode_last_known', msg.state.mode);
} else {
  var lastMode = flow.get('mode_last_known');
  if (lastMode) msg.state.mode = lastMode;
}

msg.state.recup_is_on = (String(msg.state.recup || '').toLowerCase() === 'on');

node.status({
  fill: 'green',
  shape: 'dot',
  text: `telemetry tick | CO2 ${Math.round(msg.state.co2 || 0)} | mode ${msg.state.mode || ''} | alarm ${msg.state.alarm || ''}`
});

return msg;

// Telemetrijos snapshot įrašas (STABLE)
// - schema_ver iš global
// - 45 min RATE langas (anti-flicker)
// - 0% fan logika
// - boost_active = tik vidinis NR indikatorius (source of truth)
// - rate_final publikuojam į global: komfovent.telemetry_rate (Control konsistencijai)

const TZ = 'Europe/Vilnius';
const s = msg.state || {};
const ents = global.get('komfovent_entities') || {};

// ===== versijos iš global =====
const schema_ver = Number(global.get('schema_ver') || '');
const flow_ver = String(global.get('system_version') || '');

// ===== HA states (robust) =====
let ha = global.get('homeassistant') || {};
let states =
  (ha?.homeAssistant?.states) ||
  (ha?.homeassistant?.states) ||
  (ha?.states) ||
  {};

function ltIso(dateObj) {
  return new Intl.DateTimeFormat('sv-SE', {
    timeZone: TZ,
    year: 'numeric', month: '2-digit', day: '2-digit',
    hour: '2-digit', minute: '2-digit', second: '2-digit',
    hour12: false
  }).format(dateObj).replace(' ', 'T');
}

function isNum(v) {
  return v !== null && v !== undefined && isFinite(v);
}

function clamp(n, a, b) {
  return Math.max(a, Math.min(b, n));
}

function boolFromState(entId) {
  const raw = String(states?.[entId]?.state || '').toLowerCase();
  return (raw === 'on' || raw === 'true');
}

const nowDate = new Date();
const nowMs = nowDate.getTime();
const ts = ltIso(nowDate);

const dow = new Intl.DateTimeFormat('en-US', { timeZone: TZ, weekday: 'short' }).format(nowDate);
const hour = Number(new Intl.DateTimeFormat('en-GB', { timeZone: TZ, hour: '2-digit', hour12: false }).format(nowDate));

// ===== ALARM / OCCUPIED (su fallback) =====
let alarm = s.alarm;
if (!alarm) alarm = flow.get('alarm_last_known') || null;
if (alarm) flow.set('alarm_last_known', alarm);

const occupied = (String(alarm || '') === 'disarmed');

// ===== MODE (su fallback) =====
let mode = (s.mode && String(s.mode).trim())
  ? String(s.mode).trim()
  : (flow.get('mode_last_known') || null);

if (mode) flow.set('mode_last_known', mode);

// ===== RECUP IS ON =====
let recupIsOn = (s.recup_is_on === true);

if (s.recup_is_on === undefined) {
  const recupEnt = ents.recup || 'switch.start_stop';
  recupIsOn = (String(states?.[recupEnt]?.state || '').toLowerCase() === 'on');
}

// ===== FAN actual / desired (0% LOGIC) =====
const FAN_ACTUAL_ENTITY = ents.fan_actual || 'sensor.intake_fan_level_current';
const FAN_DESIRED_ENTITY = ents.fan_desired || 'sensor.komfovent_fan_desired';

let fanActual = parseFloat(states?.[FAN_ACTUAL_ENTITY]?.state);
if (!isNum(fanActual) || fanActual === 0 || recupIsOn === false)
  fanActual = 0;
else
  fanActual = clamp(Math.round(fanActual), 20, 100);

let fanDesired = parseFloat(states?.[FAN_DESIRED_ENTITY]?.state);
if (!isNum(fanDesired) || fanDesired === 0 || recupIsOn === false)
  fanDesired = 0;
else
  fanDesired = clamp(Math.round(fanDesired), 20, 100);

// final fan
const fan = (fanDesired > 0) ? fanDesired : fanActual;

// ===== CO2 =====
const co2 = isNum(s.co2) ? Number(s.co2) : null;

// ===== RATE (45 min sliding window) =====
const RATE_WINDOW_MIN = 45;
const MAX_KEEP_MIN = 240;

let tlog = flow.get('telemetry_co2_log');
if (!Array.isArray(tlog)) tlog = [];

if (co2 !== null)
  tlog.push({ ts: nowMs, co2 });

tlog = tlog.filter(p =>
  (p && isNum(p.ts) && isNum(p.co2))
    ? (nowMs - p.ts <= (MAX_KEEP_MIN * 60000))
    : false
);

flow.set('telemetry_co2_log', tlog);

let rateWindow = null;
const win = tlog.filter(p => nowMs - p.ts <= (RATE_WINDOW_MIN * 60000));

// minimum 2 taškai ir ≥10 min skirtumas
if (win.length >= 2) {
  const first = win[0];
  const last = win[win.length - 1];
  const deltaMin = (last.ts - first.ts) / 60000;

  if (deltaMin >= 10) {
    rateWindow = Number(((last.co2 - first.co2) / deltaMin).toFixed(2)); // ppm/min
  }
}

// stabilizuotas rate_final
let rate_final = rateWindow;

if (rate_final === null || !isFinite(rate_final)) {
  const lastRate = flow.get('telemetry_last_rate');
  rate_final = isFinite(Number(lastRate)) ? Number(lastRate) : 0;
}
else if (Math.abs(rate_final) < 0.05) {
  rate_final = 0;
  flow.set('telemetry_last_rate', 0);
}
else {
  flow.set('telemetry_last_rate', rate_final);
}

// ===== PUBLISH RATE (vienas šaltinis Control'ui) =====
global.set('komfovent.telemetry_rate', rate_final);
global.set('komfovent.telemetry_rate_ts', nowMs);

// ===== BOOST (source of truth) =====
const boost_active = boolFromState(
  ents.boost_active_internal || 'binary_sensor.komfovent_rate_boost_nr'
);

// ===== LOG RECORD =====
msg.log_type = 'telemetry';
msg.log_record = {
  schema_ver,
  flow_ver,
  module: 'telemetry',
  kind: 'snapshot',

  ts,
  occupied,
  dow,
  hour,

  mode,

  fan,
  fan_actual: fanActual,
  fan_desired: fanDesired,

  co2,
  rate: rate_final,

  boost_active,

  outdoor: isNum(s.outdoor) ? Number(s.outdoor) : null,
  humidity: isNum(s.humidity) ? Number(s.humidity) : null,
  windows_long_open: !!s.windows_long_open
};

node.status({
  fill: 'blue',
  shape: 'dot',
  text: `v${flow_ver} | CO2 ${co2 ?? 'n/a'} | rate ${rate_final} | fan ${fan}% | boost ${boost_active}`
});

return msg;

// Normalizuojam visų modulių įrašus į schema v3.
// Tikimasi:
// - msg.log_type: telemetry | control | learning | filters
// - msg.log_record: object

const schema_ver = Number(global.get('schema_ver') || '');
const flow_ver = String(global.get('system_version') || '');

const type = String(msg.log_type || '').trim();
const rec = msg.log_record;

if (!type || !rec || typeof rec !== 'object') {
  node.status({ fill: 'red', shape: 'ring', text: 'missing log_type/log_record' });
  return null;
}

// module
if (!rec.module) rec.module = type;

// schema / version
rec.schema_ver = schema_ver;
rec.flow_ver = flow_ver;

// kind: jei neatėjo – priskiriam pagal tipą
if (!rec.kind) {
  if (type === 'telemetry') rec.kind = 'snapshot';
  else if (type === 'control') rec.kind = 'decision';
  else if (type === 'learning') rec.kind = 'learning_update';
  else if (type === 'filters') rec.kind = 'filter_tick';
  else rec.kind = 'unknown';
}

msg.log_record = rec;
return msg;

// Paruošiam msg.filename ir msg.payload failo append'ui.
// Tikimasi:
// - msg.log_type: 'telemetry' | 'control' | 'learning' | 'filters'
// - msg.log_record: object (schema v3)

const TZ = 'Europe/Vilnius';
const type = String(msg.log_type || '').trim();
const rec = msg.log_record;

if (!type || !rec || typeof rec !== 'object') {
  node.status({ fill: 'red', shape: 'ring', text: 'missing log_type/log_record' });
  return null;
}

function ltDate(d){
  const s = new Intl.DateTimeFormat('sv-SE', { timeZone: TZ, year:'numeric', month:'2-digit', day:'2-digit' }).format(d);
  return s;
}

const day = ltDate(new Date());

let prefix = null;
if (type === 'telemetry') prefix = 'komfovent_telemetry_';
else if (type === 'control') prefix = 'komfovent_control_';
else if (type === 'learning') prefix = 'komfovent_learning_';
else if (type === 'filters') prefix = 'komfovent_filters_';
else {
  node.status({ fill: 'red', shape: 'ring', text: `unknown log_type ${type}` });
  return null;
}

msg.filename = `/data/logs/komfovent/${prefix}${day}.jsonl`;
msg.payload = JSON.stringify(rec) + "\n";

node.status({ fill: 'blue', shape: 'dot', text: `append ${type} -> ${day}` });
return msg;

// Retention: trinam komfovent_*.jsonl senesnius nei 60 dienų.
// Vykdom container'io viduje (Node-RED konteineryje), kelias /data/logs/komfovent.

msg.payload = "find /data/logs/komfovent -type f -name 'komfovent_*.jsonl' -mtime +60 -print -delete";
return msg;

// Parodom rezultatą status'e (kad matytum, jog cleanup suveikė)

let out = msg.payload;
if (Buffer.isBuffer(out)) out = out.toString('utf8');
out = String(out || '').trim();

if (out) node.status({ fill: 'yellow', shape: 'dot', text: `cleanup deleted:\n${out.split('\n').length} files` });
else node.status({ fill: 'green', shape: 'dot', text: 'cleanup ok (nothing to delete)' });

return null;
